con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

log.info "Hello this is my first program"

//Context variable is used to access and modify the properties
//of your testcases

//scope of context lies in testcase only!

log.info context.expand('${#TestCase#name}')

log.info context.expand('${#TestCase#age}')
//To access any property you have to come through its parent only
log.info testRunner.testCase.testSuite.testCases["GetEmployee"].getPropertyValue("id")

testRunner.testCase.testSuite.testCases["GetEmployee"].setPropertyValue("id","1234")

log.info testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("age")

log.info testRunner.testCase.getPropertyValue("dept")

//log info testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["get"].getPropertyValue("Request")

log.info testRunner.testCase.testSuite.getTestCaseByName("GetEmployee").getTestStepByName("get").getPropertyValue("Request")

def project = testRunner.testCase.testSuite.project
project.getPropertyValue("gender")

// *******
//add employee request with properties from TestCase level

def addReq = testRunner.testCase.testSuite.getTestCaseByName("AddEmployee").getTestStepByName("Add").getPropertyValue("Request")
def name = testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("name")
def id = testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("id")
def xmlAdd = new XmlHolder(addReq)  //for updating this xml
def age = testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("age")

xmlAdd.setNodeValue("//typ:addEmployee/typ:name", name)
xmlAdd.setNodeValue("//typ:addEmployee/typ:id", id)
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department", "CSE")
xmlAdd.setNodeValue("//typ:addEmployee/typ:age", age)
def newAddXml = xmlAdd.getXml();

testRunner.testCase.testSuite.getTestCaseByName("AddEmployee").getTestStepByName("Add").setPropertyValue("Request",newAddXml)

//Calling the service
//context of add employee test case should pass

def addTestStep = testRunner.testCase.testSuite.getTestCaseByName("AddEmployee").getTestStepByName("Add")
def contextAddEmployee = new WsdlTestRunContext(addTestStep);

addTestStep.run(testRunner, contextAddEmployee)

//Get employee

def getReq = testRunner.testCase.testSuite.getTestCaseByName("GetEmployee").getTestStepByName("get").getPropertyValue("Request")
def xmlGet = new XmlHolder(getReq)

xmlGet.setNodeValue("//typ:getEmployeeDetails/typ:employeeName", name)
def getEmployeeXml = xmlGet.getXml();

testRunner.testCase.testSuite.getTestCaseByName("GetEmployee").getTestStepByName("get").setPropertyValue("Request",getEmployeeXml)

def getTestSteps = testRunner.testCase.testSuite.getTestCaseByName("GetEmployee").getTestStepByName("get");
def contextGetEmployee = new WsdlTestRunContext(getTestSteps);

getTestSteps.run(testRunner, contextGetEmployee)

//validation

def getResp = testRunner.testCase.testSuite.getTestCaseByName("GetEmployee").getTestStepByName("get").getPropertyValue("Response")
def xmlGetResp = new XmlHolder(getResp)

def GetResponse = xmlGetResp.getNodeValue("//ns:name")

log.info GetResponse
log.info name

assert GetResponse == name

//delete

def deleteReq = testRunner.testCase.testSuite.getTestCaseByName("DeleteEmployee").getTestStepByName("delete").getPropertyValue("Request")
def xmlDelete = new XmlHolder(deleteReq)
xmlDelete.setNodeValue("//typ:deleteEmployee/typ:employeeName", name)

def deleteEmployeeXml = xmlDelete.getXml();
testRunner.testCase.testSuite.getTestCaseByName("DeleteEmployee").getTestStepByName("delete").setPropertyValue("Request",deleteEmployeeXml)

def deleteTestSteps = testRunner.testCase.testSuite.getTestCaseByName("DeleteEmployee").getTestStepByName("delete");
def contextDeleteEmployee = new WsdlTestRunContext(deleteTestSteps);

deleteTestSteps.run(testRunner, contextDeleteEmployee)
